from bs4 import BeautifulSoup


def replace_latex_images(html_content):
    """
    Replace <img> tags containing LaTeX formulas with their alt text.

    Args:
        html_content (str): HTML content as string

    Returns:
        str: Modified HTML with images replaced by their alt text
    """
    # Parse the HTML
    soup = BeautifulSoup(html_content, 'html.parser')

    # Find all img tags
    img_tags = soup.find_all('img')

    for img in img_tags:
        # Check if it has an alt attribute with LaTeX content
        if 'alt' in img.attrs:
            alt_text = img['alt']
            # Check if it looks like LaTeX (contains { or \)
            if '{' in alt_text or '\\' in alt_text:
                # Create a span element with the LaTeX content
                # Using a span with a class for styling if needed
                latex_span = soup.new_tag('span', **{'class': 'latex-formula'})
                latex_span.string = alt_text
                img.replace_with(latex_span)

    return str(soup)


